import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import summaryApi from '../common'
import { FaStar } from "react-icons/fa";
import { FaStarHalf } from "react-icons/fa";


const ProductDetails = () => {
  const [data, setdata] = useState({
    productName: "",
    brandName: "",
    category: "",
    productImage: [],
    description: "",
    price: "",
    sellingPrice: ""
  })

  const params = useParams()
  const [loading, setloading] = useState(false)
  const productimg = new Array(4).fill(null)
  const [activeimage, setactiveimage] = useState("")


  const fetchProductDetails = async () => {
    setloading(true)
    const response = await fetch(summaryApi.productDetails.url, {
      method: summaryApi.productDetails.method,
      headers: { 'content-Type': 'application/json' },
      body: JSON.stringify({
        productId: params?.id
      })
    })
    setloading(false)
    const dataresponse = await response.json()
    setdata(dataresponse?.data)
    setactiveimage(dataresponse?.data.productImage[0])
  }

  useEffect(() => {
    fetchProductDetails()
  }, [])

  const handleMouse = (imageurl) => {
    setactiveimage(imageurl)
  }
  console.log("Data", data)



  return (
    <div className='container mx-auto p-8'>
      <div className='min-h-[200px] flex flex-col lg:flex-row gap-4'>


        {/* Product image section */}
        <div className='h-96 flex flex-col lg:flex-row-reverse gap-4'>
          <div className='h-[300px] w-[300px] lg:h-96 lg:w-96 bg-slate-200'>
            <img src={activeimage} className='w-full h-full object-scale-down mix-blend-multiply' />
          </div>



          <div className='h-full'>
            {
              loading ? (
                <div className='flex gap-2 lg:flex-col overflow-scroll scrollbar-none h-full'>
                  {
                    productimg.map(el => {
                      return (
                        <div className='h-20 w-20 bg-slate-200 rounded animate-pulse' key={'loading'}></div>
                      )
                    })
                  }
                </div>

              ) : (
                <div className='flex gap-2 lg:flex-col overflow-scroll scrollbar-none h-full'>
                  {
                    data?.productImage?.map((imageurl, index) => {
                      return (
                        <div className='h-20 w-20 bg-slate-200 rounded p-1 ' key={imageurl}>
                          <img src={imageurl} className='w-full h-full object-scale-down mix-blend-multiply cursor-pointer' onMouseEnter={(() => handleMouse(imageurl))} onClick={(() => handleMouse(imageurl))} />
                        </div>
                      )
                    })
                  }
                </div>
              )
            }
          </div>
        </div>

        {/* Product Detail Section */}
        <div className='flex flex- gap-1'>
          <p className='bg-red-200 text-red-600 px-2 rounded-full inline'>{data?.brandName}</p>
          <h2 className='text-2xl lg:text-3xl font-medium '>{data?.productName}</h2>
          <p className='capitalize text-slate-500 '>{data?.category}</p>
          <div className='text-red-600 flex items-center gap-1'>
            <FaStar/>
            <FaStar/>
            <FaStar/>
            <FaStar/>
            <FaStarHalf/>
          </div>
        </div>


      </div>
    </div>
  )
}

export default ProductDetails
