import React from 'react'
import Logo from './logo'
import { IoSearch } from "react-icons/io5";
import { FaRegUserCircle } from "react-icons/fa";
import { HiMiniShoppingCart } from "react-icons/hi2";
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import summaryApi from '../common';
import { toast } from 'react-toastify';
import { setUserDetails } from '../store/userslice';


const Headr = () => {
  const user = useSelector((state) => state?.user?.user)
  const dispatch = useDispatch()

  // console.log('user', user);
  const handlelogout = async () => {
    const fetchdata = await fetch(summaryApi.logout_user.url, {
      method: summaryApi.logout_user.method,
      credentials: 'include'
    })
    const data = await fetchdata.json();
    if (data.success) {
      toast.success(data.message);
      dispatch(setUserDetails(null));
    }
    if (data.error) {
      toast.error(data.message);
    }
  }


  return (
    <div>
      <header className='h-16 shadow-md bg-white'>
        <div className="p-5 h-full container mx-auto flex items-center justify-between">
          <div className="">
            <Link to={'/'}>
              <Logo w={100} h={50} />
            </Link>
          </div>
          <div className="hidden lg:flex items-center w-full justify-between  max-w-sm border rounded-full focus-within:shadow">
            <input type='text' className='w-full pl-2 outline-none' placeholder='search product here ...' />
            <div className='text-lg min-w-[50px] h-8 bg-red-500 flex items-center justify-center rounded-r-full text-white'><IoSearch /></div>
          </div>
          <div className="flex item-center justify-center gap-3 lg:gap-7 ">
            <div className=' cursor-pointer relative text-2xl lg:text-3xl '>
              <span><HiMiniShoppingCart /></span>
              <div className='bg-red-500 text-white flex items-center justify-center w-5 h-5 rounded-full p-1 absolute -top-2 -right-3'><p className='text-xs'>0</p></div>
            </div>
            <div className='relative group flex justify-center'>
              <div className='cursor-pointer text-2xl lg:text-3xl'>
                {
                  user?.profilePic ? (
                    <img src={user?.profilePic} className='w-10 h-10 rounded-full' alt={user?.name} />
                  ) : (<FaRegUserCircle />)
                }

              </div>
              <div className='absolute bg-white bottom-0 top-8 h-fit p-2 shadow-lg rounded'>
                <nav>
                  <Link to={'admin-panel'} className='whitespace-nowrap hover:te'>Admin Panel</Link>
                </nav>
              </div>
            </div>
            <div className='text-md lg:text-l'>
              {
                user?._id ? (

                  <button onClick={handlelogout} className='px-3 py-1 rounded-full text-white bg-red-500 hover:bg-red-700'>Logout</button>

                ) : (
                  <Link to={'/login'}>
                    <button className='px-3 py-1 rounded-full text-white bg-red-500 hover:bg-red-700'>Login</button>
                  </Link>)

              }
            </div>
          </div>
        </div>
      </header>
    </div>
  )
}

export default Headr
