import React, { useEffect, useState } from 'react'
import summaryApi from '../common'

const Cart = () => {

  const [data, setdata] = useState([])
  const [loading, setloading] = useState(false)


  const fetchdata = async () => {
    setloading(true)
    const response = await fetch(summaryApi.addtocartview.url, {
      method: summaryApi.addtocartview.method,
      credentials: "include",
      headers: { 'content-type': 'application/json' },

    })
    // setloading(false)
    const responsedata = await response.json()
    if (responsedata.success) {
      setdata(responsedata.data)
    }


  }

  useEffect(() => {
    fetchdata()
  }, [])

  console.log("cart",data)
  return (
    <div className='container  mx-auto p-4 px-8'>
      <div className='text-center text-lg '>
        {
          data.length == 0 && !loading && (
            <p className='bg-white py-2'>No Data</p>
          )
        } 
        
      </div>

      <div>
        {/* View Product */}
        <div>
          {
            loading ? (
              <div className='w-full bg-slate-200 h-32 my-1 borderanimate-pulse'>

              </div>

            ):(
              <div></div>
            )
          }
        </div>
      </div>
    </div>
  )
}

export default Cart
