import React, { useState } from 'react';
import { FaAngleLeft } from "react-icons/fa6";
import { FaAngleRight } from "react-icons/fa6";

import image1 from "../assest/banner/img1.webp";
import image2 from "../assest/banner/img2.webp";
import image3 from "../assest/banner/img3.jpg";
import image4 from "../assest/banner/img4.jpg";
import image5 from "../assest/banner/img5.webp";

import image1_mob from "../assest/banner/img1_mobile.jpg";
import image2_mob from "../assest/banner/img2_mobile.webp";
import image3_mob from "../assest/banner/img3_mobile.jpg";
import image4_mob from "../assest/banner/img4_mobile.jpg";
import image5_mob from "../assest/banner/img5_mobile.png";

const BannerProduct = () => {
  const [currentImage, setCurrentImage] = useState(0);
  const desktopImages = [image1, image2, image3, image4, image5];
  const mobileImages = [image1_mob, image2_mob, image3_mob, image4_mob, image5_mob];

  const images = window.innerWidth < 768 ? mobileImages : desktopImages;

  const nextImage = () => {
    setCurrentImage((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };

  const prevImage = () => {
    setCurrentImage((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  return (
    <div className='container mx-auto p-4 px-8 rounded'>
      <div className='relative h-80'>
        <div className='absolute top-0 left-0 right-0 bottom-0'>
          <img src={images[currentImage]} alt="" className='w-full h-full object-cover' />
        </div>
        <button onClick={prevImage} className='absolute top-1/2 left-4 transform -translate-y-1/2 bg-gray-800 text-white px-2 py-1 rounded'>
          <FaAngleLeft />
        </button>
        <button onClick={nextImage} className='absolute top-1/2 right-4 transform -translate-y-1/2 bg-gray-800 text-black bg-transparent  px-2 py-1 rounded'>
          <FaAngleRight />
        </button>
      </div>
    </div>
  );
};

export default BannerProduct;
