import React, { useEffect, useState, useRef } from 'react';
import getcategorywiseproduct from '../Helpers/getcategorywiseproduct';
import displayINRCurrency from '../Helpers/displayCurrency';
import { FaAngleRight, FaAngleLeft } from "react-icons/fa6";

const HorizontalCardProduct = ({ category, heading }) => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);
    const containerRef = useRef(null);

    const fetchData = async () => {
        setLoading(true);
        const categoryProduct = await getcategorywiseproduct(category);
        setLoading(false);
        setData(categoryProduct?.data);
    };

    useEffect(() => {
        fetchData();
    }, [category]);

    const scrollLeft = () => {
        if (containerRef.current) {
            containerRef.current.scrollBy({ left: -300, behavior: 'smooth' });
        }
    };

    const scrollRight = () => {
        if (containerRef.current) {
            containerRef.current.scrollBy({ left: 300, behavior: 'smooth' });
        }
    };

    return (
        <div className='container mx-auto px-8 py-4 relative'>
            <h2 className='text-2xl font-semibold pb-4'>
                {heading}
            </h2>
            <div className='flex items-center gap-4 md:gap-6 overflow-x-auto scrollbar-none' ref={containerRef}>
                <button 
                    className='bg-white shadow-md rounded-full p-1 absolute left-0 z-10'
                    onClick={scrollLeft}
                >
                    <FaAngleLeft />
                </button>
                {loading ? (
                    new Array(13).fill(null).map((_, index) => (
                        <div key={index} className='w-full min-w-[280px] md:min-w-[320px] max-w-[280px] md:max-w-[320px] h-40 bg-white rounded-sm shadow flex items-center justify-center'>
                            <div className='animate-pulse bg-gray-300 h-full w-full'></div>
                        </div>
                    ))
                ) : (
                    data.map((product, index) => (
                        <div key={index} className='w-full min-w-[280px] md:min-w-[320px] max-w-[280px] md:max-w-[320px] h-40 bg-white rounded-sm shadow flex'>
                            <div className='bg-slate-200 h-full p-4 min-w-[120px] md:min-w-[145px]'>
                                <img src={product.productImage[0]} className='object-scale-down h-full hover:scale-110 transition-all mix-blend-multiply' alt={product.productName} />
                            </div>
                            <div className='p-4 grid'>
                                <h2 className='font-medium text-base md:text-lg text-ellipsis line-clamp-1 text-black'>{product?.productName}</h2>
                                <p className='capitalize text-slate-500'>{product?.category}</p>
                                <div className='flex gap-3'>
                                    <p className='text-red-500 font-medium'>{displayINRCurrency(product?.sellingPrice)}</p>
                                    <p className='text-slate-500 line-through'>{displayINRCurrency(product?.price)}</p>
                                </div>
                                <button className='text-sm mt-1 bg-red-500 hover:bg-red-700 text-white px-3 py-0.5 rounded-full'>Add to cart</button>
                            </div>
                        </div>
                    ))
                )}
                <button 
                    className='bg-white shadow-md rounded-full p-1 absolute right-0 z-10'
                    onClick={scrollRight}
                >
                    <FaAngleRight />
                </button>
            </div>
        </div>
    );
};

export default HorizontalCardProduct;
