import summaryApi from "../common";

const addToCart = async (e,id)=>{
    e?.stopPropagation()
    e?.preventDefault()

    const response = await fetch(summaryApi.addToCartProduct.url,{
        method:summaryApi.addToCartProduct.method,
        credentials:'include',
        
    })
    
}
