import React, { useState } from 'react'
import loginIcon from '../assest/signin.gif'
import { IoEye } from "react-icons/io5";
import { IoIosEyeOff } from "react-icons/io";



const Login = () => {

    const [ShowPassword, SetshowPassword] = useState(false);
    function change() {
        if (ShowPassword == false) {
            SetshowPassword(true);
        }
        else {
            SetshowPassword(false);
        }
    }

    return (
        <section id='login'>
            <div className='mx-auto container p-4'>

                <div className='bg-white p-2 py-4 w-full max-w-md mx-auto'>
                    <div className='w-20 h-20 mx-auto bg-white'>
                        <img className='' src={loginIcon} alt='login-icon' />
                    </div>
                    <form>
                        <div className='grid'>
                            <label>Email:</label>
                            <div className='bg-slate-100 p-2'>
                                <input type='email' name='email' className='w-full h-full outline-none bg-transparent' placeholder='Enter Email' />
                            </div>
                        </div>

                        <div>
                            <label>Password:</label>
                            <div className='bg-slate-100 p-2 flex'>
                                <input type={ShowPassword ? "text" : "password"} name='Password' className='w-full h-full outline-none bg-transparent' placeholder='Enter Password' />
                                <div className='flex items-center cursor-pointer' onClick>
                                    {
                                        ShowPassword ? (<IoEye />) : (<IoIosEyeOff />)
                                    }
                                </div>
                            </div>

                        </div>

                        <button>Login</button>
                    </form>

                </div>


            </div>
        </section>
    )
}

export default Login
