import React from 'react'
import ROLE from '../common/role'

const ChangeUserRole = () => {
    return (
        <div className='fixed top-0 bottom-0 left-0 right-0 w-full h-full z-10 flex justify-between items-center '>
            <div className=' mx-auto bg-white shadow-md p-4 max-w-sm'>

                <h1 className='pb-4 text-lg font-medium'>Change User Role</h1>

                <p>Name:</p>
                <p>Email:</p>
                <div className='flex'>

                
                <p>Role: </p>
                <select className='border px-4 py-1'>
                    {
                        Object.values(ROLE).map(e1 => {
                            return (
                                <option value={e1} key={e1}>
                                    {e1}
                                </option>
                            )
                        })
                    }
                </select>
                </div>
            </div>
        </div>
    )
}

export default ChangeUserRole
