import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import summaryApi from '../common'

const ProductDetails = () => {
  const [data, setdata] = useState({
    productName: "",
    brandName: "",
    category: "",
    productImage: [],
    description: "",
    price: "",
    sellingPrice: ""
  })

  const params = useParams()
  const [loading, setloading] = useState(false)
  const productimg = new Array(4).fill(null)


  const fetchProductDetails = async () => {
    setloading(true)
    const response = await fetch(summaryApi.productDetails.url, {
      method: summaryApi.productDetails.method,
      headers: { 'content-Type': 'application/json' },
      body: JSON.stringify({
        productId: params?.id
      })
    })
    // setloading(false)
    const dataresponse = await response.json()
    setdata(dataresponse?.data)
  }

  useEffect(() => {
    fetchProductDetails()
  }, [])
  console.log("Data", data)



  return (
    <div className='container mx-auto p-8'>
      <div className='min-h-[200px]'>


        <div>
          <div >
            {
              loading ? (
                <div className='flex gap-2 lg:flex-col overflow-scroll scrollbar-none h-full'>
                  {
                    productimg.map(el => {
                      return (
                        <div className='h-20 w-20 bg-slate-200 rounded animate-pulse' key={'loading'}></div>
                      )
                    })
                  }
                </div>

              ) : (
                <div className='flex gap-2 lg:flex-col overflow-scroll scrollbar-none h-full'>
                  {
                    data.productImage.map((imageurl,index) => {
                      return (
                        <div className='h-20 w-20 bg-slate-200 rounded p-1 ' key={imageurl}>
                          <img src={imageurl} className='w-full h-full object-scale-down mix-blend-multiply'/>
                        </div>
                      )
                    })
                  }
                </div>
              )
            }
          </div>
        </div>


        <div>
        </div>


        <div>
        </div>
      </div>

    </div>
  )
}

export default ProductDetails
