import React from 'react'
import { FaRegUserCircle } from "react-icons/fa";
import {  useSelector } from 'react-redux';


const AdminPanel = () => {
    const user = useSelector((state) => state?.user?.user)

    return (
        <div className='min-h-[calc(100vh-120px)] flex '>
            <aside className='bg-white min-h-full w-full max-w-60 customShadow'>
                <div className='h-32  flex justify-center flex-col items-center '>
                    <div className='cursor-pointer flex justify-center text-5xl' >
                        {
                            user?.profilePic ? (
                                <img src={user?.profilePic} className='w-8 h-8 rounded-full' alt={user?.name} />
                            ) : (<FaRegUserCircle />)
                        }

                    </div>
                    <p className='capitalize text-lg font-semibold '>{user?.name}</p>
                    <p className='capitalize text-lg font-semibold '>{user?.role}</p>

                </div>
            </aside >
            <main>
                main
            </main>
        </div >
    )
}

export default AdminPanel
