import React, { useEffect, useState } from 'react'
import getcategorywiseproduct from '../Helpers/getcategorywiseproduct'
import displayINRCurrency from '../Helpers/displayCurrency';
import { FaAngleRight } from "react-icons/fa6";
import { FaAngleLeft } from "react-icons/fa6";

const HoriontalCardProduct = ({ category, heading }) => {
    const [data, setdata] = useState([])
    const [loadings, setloading] = useState(false)

    const loadinglist = new Array(13).fill(null)
    const fetchdata = async () => {
        setloading(true)
        const categoryproduct = await getcategorywiseproduct(category)
        setloading(false)
        setdata(categoryproduct?.data)
    }
    useEffect(() => {
        fetchdata()
    }, [])


    return (
        <div className='container  mx-auto px-8  py-4 relative'>
            <h2 className='text-2xl  font-semibold pb-4'>
                {heading}
            </h2>

            <div className='flex items-center gap-4 md:gap-6 overflow-x-auto scrollbar-none transition-all'>
                <button className='bg-white shadow-md rounded-full p-1  absolute left-3 z-10'><FaAngleLeft /></button>
                <button className='bg-white shadow-md rounded-full p-1  absolute right-3 z-10 '><FaAngleRight /></button>
                {
                    data.map((product, index) => {
                        return (
                            <div className='w-full min-w-[364px] md:min-w-[320px] max-w-[280px] md:max-w-[320px] h-40 bg-white rounded-sm shadow flex '>
                                <div className='bg-slate-200 h-full p-4 min-w-[120px] md:min-w-[145px]'>
                                    <img src={product.productImage[0]} className='object-scale-down h-full hover:scale-110 transition-all mix-blend-multiply' />
                                </div>
                                <div className='p-4 grid'>
                                    <h2 className='font-medium text-base md:text-lg text-ellipsis line-clamp-1 text-black'>{product?.productName}</h2>
                                    <p className='capitalize text-slate-500 '>{product?.category}</p>
                                    <div className='flex gap-3'>
                                        <p className='text-red-500 font-medium'>{displayINRCurrency(product?.sellingPrice)}</p>
                                        <p className='text-slate-500 line-through'>{displayINRCurrency(product?.price)}</p>
                                    </div>
                                    <button className='text-sm mt-1 bg-red-500 hover:bg-red-700 text-white px-3 py-0.5 rounded-full '>Add to cart</button>
                                </div>
                            </div>
                        )
                    })
                }

            </div>

        </div>
    )
}

export default HoriontalCardProduct
