import React, { useState } from 'react'
import loginIcon from '../assest/signin.gif'
import { IoEye } from "react-icons/io5";
import { IoIosEyeOff } from "react-icons/io";
import { Link, Navigate, useNavigate } from 'react-router-dom';
import summaryApi from '../common';
import { toast } from 'react-toastify';


const Login = () => {

    const [ShowPassword, SetshowPassword] = useState(false);
    const [data, setdata] = useState({
        email: '',
        password: ''
    })
    const navigate = useNavigate();
    const handleOnchange = (e) => {
        const { name, value } = e.target

        setdata((preve) => {
            return {
                ...preve,
                [name]: value
            }
        })
    }
    console.log("Data Login", data);

    const handleSubmit = async (e) => {
        e.preventDefault()

        const dataresponse = await fetch(summaryApi.SignIn.url, {
            method: summaryApi.SignIn.method,
            credentials: "include",
            headers: {
                "content-type": "application/json",

            },
            body: JSON.stringify(data)
        });
        const dataApi = await dataresponse.json();
        if (dataApi.success) {
            toast.success(dataApi.message);
            navigate('/');
        }
        else {
            toast.error(dataApi.message);
        }
    }

    return (
        <section id='login'>
            <div className='mx-auto container p-4'>

                <div className='bg-white p-4 py-4 w-full max-w-md mx-auto'>
                    <div className='w-20 h-20 mx-auto bg-white'>
                        <img className='' src={loginIcon} alt='login-icon' />
                    </div>
                    <form className='pt-6 flex flex-col gap-3' onSubmit={handleSubmit}>
                        <div className='grid'>
                            <label>Email:</label>
                            <div className='bg-slate-100 p-2'>
                                <input type='email' name='email' value={data.email} onChange={handleOnchange} className='w-full h-full outline-none bg-transparent' placeholder='Enter Email' />
                            </div>
                        </div>

                        <div>
                            <label>Password:</label>
                            <div className='bg-slate-100 p-2 flex'>
                                <input type={ShowPassword ? "text" : "password"} name='password' value={data.password} onChange={handleOnchange} className='w-full h-full outline-none bg-transparent' placeholder='Enter Password' />
                                <div className='flex items-center cursor-pointer' onClick={() => SetshowPassword((preve) => !preve)}>
                                    {
                                        ShowPassword ? (<IoEye />) : (<IoIosEyeOff />)
                                    }
                                </div>
                            </div>
                            <Link to={'/forgot-password'} className='block w-fit ml-auto hover:underline hover:text-red-600'>
                                Forget Password ?
                            </Link>
                        </div>

                        <button className='bg-red-600 text-white w-full  px-6 py-2 rounded-full max-w-[120px] hover:scale-110 transistion-all mx-auto block mt-3'>Login</button>
                    </form>
                    <p className='my-5'>Don't have account? <Link to={'/sign-up'} className='text-red-600 hover:underline hover:text-red-700'>Sign-Up</Link></p>
                </div>


            </div>
        </section>
    )
}

export default Login
