import React, { useContext, useEffect, useState } from 'react'
import summaryApi from '../common'
import Context from '../context'
import displayINRCurrency from '../Helpers/displayCurrency';


const Cart = () => {

  const [data, setdata] = useState([])
  const [loading, setloading] = useState(false)


  const context = useContext(Context)
  const loadingCart = new Array(context.cartproductcount).fill(null)






  const fetchdata = async () => {
    setloading(true)
    const response = await fetch(summaryApi.addtocartview.url, {
      method: summaryApi.addtocartview.method,
      credentials: "include",
      headers: { 'content-type': 'application/json' },

    })
    setloading(false)
    const responsedata = await response.json()
    if (responsedata.success) {
      setdata(responsedata.data)
    }


  }

  useEffect(() => {
    fetchdata()
  }, [])

  const increaseQty = (id)=>{

  }









  console.log("cart", data)
  return (
    <div className='container  mx-auto p-4 px-8'>
      <div className='text-center text-lg '>
        {
          data.length == 0 && !loading && (
            <p className='bg-white py-2'>No Data</p>
          )
        }

      </div>

      <div className='flex flex-col lg:justify-between lg:flex-row gap-10 p-4'>
        {/* View Product */}
        <div className='w-full max-w-3xl  '>
          {
            loading ? (
              loadingCart.map(el => {
                return (
                  <div key={el + "Add To Cart"} className='w-full bg-slate-200 h-32 my-2 border-slate-300 animate-pulse rounded'>

                  </div>

                )
              })

            ) : (
              data.map((product, index) => {
                return (
                  <div key={product?._id + "Add To Cart"} className='w-full bg-white h-32 my-2 border-slate-300  rounded grid grid-cols-[128px,1fr]'>
                    <div className='w-32 h-32 bg-slate-200 '>
                      <img src={product?.productId?.productImage[0]} className='w-full h-full object-scale-down mix-blend-multiply p-2' />
                    </div>
                    <div className='p-4 '>
                      <h2 className='text-lg lg:text-xl text-ellipsis line-clamp-1 '>{product?.productId?.productName}</h2>
                      <p className='capitalize text-slate-500'>{product?.productId?.category}</p>
                      <p className='text-red-600 font-medium text-lg'>{displayINRCurrency(product?.productId?.sellingPrice)}</p>
                      
                      <div className='flex items-center gap-3 mt-2'>
                        <button className=' border border-red-600 text-red-600 w-6 h-6 flex justify-center items-center rounded hover:bg-red-600 hover:text-white'>-</button>
                        <span>{product?.quantity}</span>
                        <button className=' border border-red-600 text-red-600 w-6 h-6 flex justify-center items-center rounded hover:bg-red-600 hover:text-white'>+</button>

                      </div>
                    </div>
                  </div>
                )
              })
            )
          }
        </div>


        {/* total product  */}
        <div className='mt-5 lg:mt-0 w-full max-w-sm'>
          {loading ? (
            <div className='h-36 bg-slate-200 border border-slate-200 animate-pulse'>
              total
            </div>
          ) :
            (
              <div className='h-36 bg-white'>
                total
              </div>
            )
          }

        </div>


      </div>
    </div>
  )
}

export default Cart
